def floor_calculator(instructions)
  floor = 0
  position = 0
  instructions = instructions.split("")

  instructions.each do |i|
    position += 1

    if (i == "(")
      floor += 1
    elsif (i == ")")
      floor -= 1
    end

    if floor == -1
      return position
    end
  end
end

